function App() {
  const handlePrint = () =>{
    window.print()
  }
  return (
    <>
    <main>
      {/*Header */}
        <header>
            <div>
              <h2>Invoicer</h2>
            </div>
            <div>
              <ul>
                <li>
                  <button onClick={handlePrint}>Print</button></li>
                <li>Download</li>
                <li>Send</li>
              </ul>
            </div>
          </header>
          {/* End of Header */} 
{/*Your Details */}
          <section>    
          <h2>Kudzai Bosha</h2>
          <p>Your Address</p>
          </section>
{/*End of your Details */}     

 {/*Client Details*/}
          <section>
            <h2>Client's Name</h2>
            <p>Client Address</p>
          </section>


 {/*End Of Client Details*/}


 {/*Dates*/}
 <article>
  <ul>
    <li>Invoice Number:</li>
    <li>Invoice Date:</li>
    <li>Due Date:</li>
  </ul>
  </article>
   {/*End of Dates*/}


    {/*Table*/}

    {/*End of Table*/}

    {/*Notes*/}
    <section>
      {/*Text Area*/}
      <p>Notes to the client</p>
    </section>

 {/*Footer*/}
 <li>Your Name</li>
 <li>Your Email</li>
 <li>Your Phone Number</li>
 <li>Bank</li>
 <li>Account Holder name</li>
 <li>Account Number</li>
    </main>


    </>
  );
}

export default App;
